// src/app/pages/evento-detalle/evento-detalle.component.ts

import { Component, OnInit } from '@angular/core';
import { ActivatedRoute }      from '@angular/router';
import { EventoService }       from '../../services/evento.service';
import { EquipoService }       from '../../services/equipo.service';

// Importaciones para PDF
import { jsPDF } from 'jspdf';
import autoTable from 'jspdf-autotable';


@Component({
  selector: 'app-evento-detalle',
  standalone: false,
  templateUrl: './evento-detalle.component.html',
  styleUrls: ['./evento-detalle.component.css']
})
export class EventoDetalleComponent implements OnInit {
  eventoId!: string;
  evento: any;
  equipos: any[] = [];

  constructor(
    private route: ActivatedRoute,
    private eventoService: EventoService,
    private equipoService: EquipoService
  ) {}

  ngOnInit(): void {
    this.eventoId = this.route.snapshot.paramMap.get('id')!;
    this.eventoService.getEventoPorId(this.eventoId).subscribe({
      next: data => this.evento = data,
      error: err => console.error('Error al obtener el evento', err)
    });
    this.equipoService.getEquiposPorEvento(this.eventoId).subscribe({
      next: data => this.equipos = data,
      error: err => console.error('Error al obtener equipos', err)
    });
  }

  downloadPdf(): void {
    const doc = new jsPDF();
    const pageWidth = doc.internal.pageSize.getWidth();
    const pageHeight = doc.internal.pageSize.getHeight();
    const margin = 20;
    let y = 20;
    
    // Logo izquierdo (arriba)
    const logoIzquierdoBase64 = 'data:image/png;base64,'; // tu logo izquierdo
    doc.addImage(logoIzquierdoBase64, 'PNG', margin, y, 30, 30);
    
    // Logo derecho (arriba)
    const logoDerechoBase64 = 'data:image/png;base64,'; // tu logo derecho
    const logoAncho = 45;
    const logoAlto = 30;
    const posXDerecha = pageWidth - margin - logoAncho;
    doc.addImage(logoDerechoBase64, 'PNG', posXDerecha, y, logoAncho, logoAlto);

    y += 35;
  
    // TÍTULOS CENTRADOS
    doc.setFontSize(16);
    doc.setFont('helvetica', 'bold');
    doc.text('CONVOCATORIA', 105, y, { align: 'center' }); y += 10;
  
    doc.setFontSize(14);
    doc.text(this.evento.nombre.toUpperCase(), 105, y, { align: 'center' }); y += 10;
  
    // SECCIÓN: DATOS GENERALES
    doc.setFontSize(12);
    doc.setFont('helvetica', 'bold');
    doc.text('Descripción del evento:', margin, y); y += 6;
    doc.setFont('helvetica', 'normal');
    doc.text(this.evento.descripcion, margin, y); y += 8;
  
    doc.setFont('helvetica', 'bold');
    doc.text('Categoría:', margin, y); y += 6;
    doc.setFont('helvetica', 'normal');
    doc.text(`${this.evento.tipo.rama} – ${this.evento.tipo.deporte}`, margin, y); y += 8;
  
    doc.setFont('helvetica', 'bold');
    doc.text('Fechas del evento:', margin, y); y += 6;
    doc.setFont('helvetica', 'normal');
    doc.text(
      `${new Date(this.evento.fechaInicio).toLocaleDateString()} al ${new Date(this.evento.fechaFin).toLocaleDateString()}`,
      margin, y
    );
    y += 8;
  
    doc.setFont('helvetica', 'bold');
    doc.text('Hora de inicio:', margin, y); y += 6;
    doc.setFont('helvetica', 'normal');
    doc.text(this.evento.horaInicio, margin, y); y += 10;
  
    // SECCIÓN: LUGAR
    doc.setFont('helvetica', 'bold');
    doc.text('Lugar:', margin, y); y += 6;
    doc.setFont('helvetica', 'normal');
    doc.text(`${this.evento.lugar.nombre}, ${this.evento.lugar.direccion}`, margin, y); y += 6;
    doc.text(`Capacidad: ${this.evento.lugar.capacidad}`, margin, y); y += 10;
  
    // SECCIÓN: CONTACTO
    doc.setFont('helvetica', 'bold');
    doc.text('Contacto del evento:', margin, y); y += 6;
    doc.setFont('helvetica', 'normal');
    doc.text(`Profesor: ${this.evento.contacto.profesor}`, margin, y); y += 6;
    doc.text(`Horario de atención: ${this.evento.contacto.horarios}`, margin, y); y += 10;
  
    // SECCIÓN: INSCRIPCIONES
    doc.setFont('helvetica', 'bold');
    doc.text('Periodo de inscripciones:', margin, y); y += 6;
    doc.setFont('helvetica', 'normal');
    doc.text(
      `Desde: ${new Date(this.evento.inscripciones.fechaInicio).toLocaleDateString()} - ${this.evento.inscripciones.horaInicio}`,
      margin, y
    ); y += 6;
    doc.text(
      `Hasta: ${new Date(this.evento.inscripciones.fechaFin).toLocaleDateString()} - ${this.evento.inscripciones.horaFin}`,
      margin, y
    );
    y += 10;
  
    // TABLA: EQUIPOS REGISTRADOS
    if (this.equipos.length) {
      doc.setFont('helvetica', 'bold');
      doc.text('Equipos Registrados:', margin, y); y += 4;
  
      autoTable(doc, {
        startY: y,
        head: [['Nombre del equipo', 'Rama', 'Deporte', 'Integrantes']],
        body: this.equipos.map(eq => [
          eq.nombre,
          eq.rama,
          eq.deporte,
          eq.integrantes.map((i: any) => `${i.nombre} (${i.noCuenta})`).join(', ')
        ]),
        margin: { left: margin, right: margin },
        styles: { fontSize: 9 },
        headStyles: { fillColor: [22, 160, 133] }
      });
    }


    // Logo final (abajo centrado)
    const logoInferiorBase64 = 'data:image/jpeg;base64,'; // tu logo inferior
    const logoInferiorAncho = 50;
    const logoInferiorAlto = 20;
    const posXInferior = (pageWidth - logoInferiorAncho) / 2;
    const posYInferior = pageHeight - margin - logoInferiorAlto;

    doc.addImage(logoInferiorBase64, 'PNG', posXInferior, posYInferior, logoInferiorAncho, logoInferiorAlto);


    //Se crea el PDF
    doc.save(`Convocatoria_${this.evento.nombre}.pdf`);
  }
  
  
}


